mapper.select:
SELECT a.staff_id,a.staff_code,a.staff_name,a.sex,a.phone,a.family_phone,a.card,a.age,a.education, a.school_name,a.major,a.org_id,a.status,DATE_FORMAT(a.create_date,'%Y-%c-%d %h:%i:%s') create_date, (select org_name from sys_org b where a.org_id = b.org_id) org_name FROM sys_staff a where 1=1
and staff_id=#{staff_id}
and (staff_name like '%${staff_name}%' or staff_code = #{staff_name})

mapper.update:
UPDATE 
  sys_staff 
SET
  password = #{password} 
WHERE staff_id = #{staff_id}

mapper.select:
SELECT a.role_id,a.role_name,a.role_desc,b.rel_id
	FROM sys_role a INNER JOIN sys_staff_role_rel b ON a.role_id = b.role_id 
WHERE b.staff_id = #{staff_id}
